const mongoose = require('mongoose');

// Evaluation model to store AI feedback for HR-style answers
const evaluationSchema = new mongoose.Schema(
  {
    // Optional link to a user if you want to track who answered
    user: {
      type: mongoose.Schema.Types.ObjectId,
      ref: 'User',
      required: false,
    },
    // The original answer text written by the candidate
    answerText: {
      type: String,
      required: true,
    },
    // Feedback text generated by the AI model
    feedback: {
      type: String,
      required: true,
    },
    // Numeric score from the AI, for example 0â€“10
    score: {
      type: Number,
      required: true,
    },
  },
  {
    timestamps: true, // createdAt will show when the evaluation happened
  }
);

const Evaluation = mongoose.model('Evaluation', evaluationSchema);

module.exports = Evaluation;


